import React, { Component } from 'react'

export default class FilmRow extends Component {
    render() {
        // let FilmRows = this.props.films.map( (film, index) => ([film[index]))
       // let FilmRows = this.props.films.map( (film, index) => (this.props.films[index].poster_path))
        var images =   this.props.films[0].poster_path

           return (
            <div className="film-row">
            <img src={`https://image.tmdb.org/t/p/w500${images}`} alt="" />
            <div className="film-summary">
                <h1>TITLE</h1>
                <p>YEAR</p>
            </div>
            </div>
        )
    }
}
