import React, { Component } from 'react'
import Fave from "./Fave"
import './index.css'


export default class FilmRow extends Component {

    constructor(props){
        super(props)
    }
    handleDetailsClick = (film) => {
        console.log("Fetching details for "+film)
    }
    render() {
        let date = new Date(this.props.newdate)
        return (
            <div onClick={() => this.handleDetailsClick(this.props.title)} className="film-row">
                <img src={`https://image.tmdb.org/t/p/w780${this.props.img}`} alt="" />
                <div className="film-summary">
                    <Fave onFaveToggle={this.props.onFaveToggle} />
                    <h1>{this.props.title}</h1>
                    <p>{date.getFullYear()}</p>
                </div>
            </div>
        )
    }
}
