export default class FilmRow extends Component {
    render() {
        var FilmRows 
        let images = this.props.films.map( (film, index) => (<img src={`https://image.tmdb.org/t/p/w780${this.props.films[index].poster_path}`} alt="" />))
        let Title = this.props.films.map( (film, index) => (<h1>{this.props.films[index].title}</h1>))

        return (
            <div className="film-row">

            <div className="film-summary">
            {Title}
                <p>YEAR</p>
                {images}

            </div>
            </div>
        )
    }
}
